'use stric';

import data from './data/searchResults.json'
import countries from './data/countries.json'
console.log(data);

const REQUEST_HOLD_TIME = 1000;

async function searchForCountry(searchText, country) {
    return new Promise((resolve, _reject) => {
        setTimeout(() => {
            const results = data.filter(d => {
                if(country)
                    return d.name.toLowerCase().startsWith(searchText.toLowerCase()) &&
                        d.country === country;
                else   
                    return d.name.toLowerCase().startsWith(searchText.toLowerCase());
            })
            console.log('--------Results------------');
            console.log(results);
            resolve(results);
        }, REQUEST_HOLD_TIME)
    });
}

async function getCountries() {
    return new Promise((resolve, _reject) => {
        setTimeout(() => {
            console.log('Countries ------------');
            console.log(countries);
            resolve(countries);
        }, REQUEST_HOLD_TIME)
    })
}

export const defaultHeaders = {
    "Content-Type": "application/json",
    "Authorization": ""
};

export default async function Api({
    url,
    method = 'GET',
    data,
    headers = defaultHeaders,
}) {
    console.log(`-------request url------${url}`);
    if (url === 'searchcountry') {
        console.log('invokeing call')
        return searchForCountry(data.searchText, data.countryCode);
    } else if(url === 'getcountries') {
        return getCountries();
    } else {
        return []
    }
}
