import React, { Fragment } from 'react';

class SearchPage extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      searchText : '',
      searchResults : [],
    }

    this.searchTextChange = this.searchTextChange.bind(this);
  }

  searchTextChange = (e) => {
    this.setState({searchText: e.target.value});
  }

  render() {
    const companies = this.state.searchResults ?? [];
    return (<Fragment>
      <div class="field">
        <div class="control">
          <input class="input is-primary" onChange={this.searchTextChange} type="text" placeholder="Search" />

        </div>

      </div>
      <button class="button">Search</button>
      <div>
        {
          this.state.searchText && (
            <p>Search Results for {this.state.searchText}</p>
          )
        }
      </div>
      <div>
        {
          companies && companies.length > 0 && companies.map((c,i) => {
            return (
              <Fragment key={i.toString()}>
                {c}
              </Fragment>
            )
          })

        }
      </div>
    </Fragment>);
  }
}

export default SearchPage;
